import React, { useState, useEffect, useContext, useRef } from 'react'
import classnames from 'classnames'

import { ThemeContext, DARK, LIGHT } from 'contexts'

import Search from 'components/Search'
import Filter from 'components/Filter'

export default function FilterPanel({ term, handlerSearchTerm }) {
  const [theme,] = useContext(ThemeContext)

  const borderColor = classnames({
    'border-secondary': theme === DARK
  })

  return (
    <form className={`filter-panel d-flex py-3 border-bottom ${borderColor}`} onSubmit={(e) => e.preventDefault()}>
      <fieldset className='flex-grow-1 me-3'>
        <Search term={term} handlerSearchTerm={handlerSearchTerm} />
      </fieldset>
      <Filter />
    </form>
  )
}