import React, { useContext, useEffect, useState } from 'react'

import { ThemeContext, LIGHT, DARK } from 'contexts'
import { CurrentUserContext } from 'contexts'

import avatar from 'assets/images/avatar.png'
import './footer.css'

export default function Footer() {
  const [theme,] = useContext(ThemeContext)
  const [user,] = useContext(CurrentUserContext)

  const borderColor = theme === DARK ? 'border-secondary' : ''
  const textColor = theme === DARK ? 'text-info' : ''

  return (
    <footer className={`footer mt-3 ${textColor}`}>
      <div className={`container py-2 py-md-4 py-sm-3 border-top ${borderColor}`}>
        <div className='row align-items-center justify-content-center'>
          <nav className="col-md-5 justify-content-center justify-content-md-end nav align-items-center">
            <span className='text-secondary'>&copy; {new Date().getUTCFullYear()} {user.name}</span>
            {/* <a className={`nav-link ${textColor}`} href="#">Link 2</a> */}
          </nav>
          <a href='/' className='col-1 d-none d-lg-block text-center'>
            <img src={avatar} className='img-fluid w-50' alt='' />
          </a>
          <nav className="col-md-5 justify-content-center justify-content-md-start nav align-items-center">
            {/* <a className={`nav-link ${textColor}`} href="#"></a> */}
          </nav>
        </div>
      </div>
    </footer>
  )
}