import React, { useContext } from 'react'
import classnames from 'classnames'

export function Modal({ display, handleClose, repoName, dirName }) {

  const modalClass = classnames({
    'd-block': display === true,
    'show': display === true,
  })

  return (
    <div className={`modal fade ${modalClass}`}>
      <div className="modal-dialog modal-fullscreen">
        <div className={`modal-content`}>
          <div className="modal-header justify-content-center">
            <h2 className="modal-title text-center text-dark">{dirName}</h2>
            <button type="button" className="btn-close mx-5" aria-label="Close" onClick={handleClose}></button>
          </div>
          <div className="modal-body overflow-hidden p-0">
            <iframe
              src={`https://weynemeynen.github.io/${repoName}/${dirName}`}
              className='h-100 w-100'
              scrolling='no'
              seamless
            />
          </div>
        </div>
      </div>
    </div>
  )
}