import React from 'react'
import classnames from 'classnames'

export function Modal({ display, handleClose, repoName, dirName }) {

  const modalClass = classnames({
    'd-block': display === true,
    'show': display === true,
  })

  return (
    <div className={`modal fade text-dark ${modalClass}`}>
      <div className="modal-dialog modal-fullscreen">
        <div className={`modal-content`}>
          <div className="modal-header justify-content-center">
            <h2 className="modal-title d-flex align-items-center">
              <span className='display-5'>
                {dirName.split('-').join(' ')}
              </span>
              <button type="button" className="border border-dark btn-close fs-6 ms-4 mt-0 rounded-circle" aria-label="Close" onClick={handleClose}></button>
            </h2>
          </div>
          <div className="modal-body overflow-hidden p-0 ratio ratio-16x9">
            <iframe
              src={`https://weynemeynen.github.io/${repoName}/${dirName}`}
              allowFullScreen
              seamless
            />
          </div>
        </div>
      </div>
    </div>
  )
}