import React, { useEffect, useContext, useState } from 'react'
import classNames from "classnames";

import { ThemeContext } from 'contexts'
// import useFetch from 'hooks/useFetch'
// import { getDummyRepos } from 'dummy'

export default function Navbar() {
  // const apiUrl = '/repos'
  // const [{ response }, doFetch] = useFetch(apiUrl)
  // const [data, setData] = useContext(DataContext)
  // const [sourseData, setSourseData] = useState(null)
  const themeContext = useContext(ThemeContext)
  const [theme, setTheme] = useState(themeContext)

  useEffect(() => {
    setTheme(theme)
    document.body.className = theme
    // setData(getDummyRepos())
  }, [theme])

  // console.log('data', data)

  // const switchSourseData = () => {
  //   if (sourseData) {
  //     setData(getDummyRepos())
  //     setSourseData(false)
  //   } else {
  //     doFetch()
  //     setData(response)
  //     setSourseData(true)
  //   }
  // }

  function switchTheme() {
    setTheme(theme === 'dark' ? 'light' : 'dark')
  }

  const themeClasses = classNames({
    'bi-moon-stars-fill': theme === 'light',
    'bi-sun': theme === 'dark',
    'text-white': theme === 'dark'
  })
  const sourseDataClasses = classNames('bi-toggle-off', {
    'text-white': theme === 'dark'
  })

  return (
    <nav className={`navbar navbar-expand-sm navbar-${theme} bg-${theme}`}>
      <div className="container">
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <div className="navbar-nav me-auto">
            <a className="nav-item nav-link active" href="#">Home</a>
            <a className="nav-item nav-link" href="#">Link</a>
          </div>
        </div>
        <div className="btn-group" role="group">
          <i className={`me-3 ${themeClasses}`} role='button' onClick={switchTheme} title='переключить цвет.тему'></i>
          <i className={`${sourseDataClasses}`} role='button' title='переключить источник данных'></i>
        </div>
      </div>
    </nav>
  )
}