import React, { useEffect, useContext } from 'react'
import classNames from "classnames";
import { Link } from 'react-router-dom';

import { ThemeContext, LIGHT, DARK } from 'contexts'

export default function Navbar() {
  const [theme, setTheme] = useContext(ThemeContext)

  useEffect(() => {
    document.body.className = `bg-${theme}`
  }, [theme])

  function switchTheme() {
    setTheme(theme === DARK ? LIGHT : DARK)
  }

  const themeClasses = classNames({
    'bi-moon-stars': theme === LIGHT,
    'bi-sun': theme === DARK
  })

  return (
    <nav className={`navbar navbar-expand-sm navbar-dark bg-dark py-3`}>
      <div className="container">
        <button className="navbar-toggler col-10" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse flex-grow-0 navbar-collapse" id="navbarSupportedContent">
          <div className="navbar-nav">
            <Link to="/" className="nav-item nav-link active">Портфолио</Link>
          </div>
        </div>
        <div className="btn-group col-2 justify-content-end pe-3 pe-sm-0" role="group">
          <i className={`text-white ${themeClasses}`}
            role='button'
            onClick={switchTheme}
            title='переключить цвет.тему'></i>
        </div>
      </div>
    </nav>
  )
}