import React, { useEffect, useContext } from 'react'
import classNames from "classnames";

import { ThemeContext } from 'contexts'

export default function Navbar() {
  const [theme, setTheme] = useContext(ThemeContext)

  useEffect(() => {
    switchTheme()
  }, [])

  function switchTheme() {
    setTheme(theme => {
      theme = theme === 'dark' ? 'light' : 'dark'
      document.body.className = `bg-${theme}`
      return theme
    })
  }

  const themeClasses = classNames({
    'bi-moon-stars': theme === 'light',
    'bi-sun': theme === 'dark'
  })

  return (
    <nav className={`navbar navbar-expand-sm navbar-dark bg-dark`}>
      <div className="container">
        <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className="collapse navbar-collapse" id="navbarSupportedContent">
          <div className="navbar-nav me-auto">
            <a className="nav-item nav-link active" href="#">Home</a>
            <a className="nav-item nav-link" href="#">Link</a>
          </div>
        </div>
        <div className="btn-group" role="group">
          <i className={`me-3 text-white ${themeClasses}`}
            role='button'
            onClick={switchTheme}
            title='переключить цвет.тему'></i>
          <i className='text-white bi-toggle-off' role='button' title='переключить источник данных'></i>
        </div>
      </div>
    </nav>
  )
}