import React, { useEffect, useContext, useState } from 'react'
import classNames from "classnames";
import { Link } from 'react-router-dom';

import { ThemeContext, LIGHT, DARK } from 'contexts'

export default function Navbar() {
  const [theme, setTheme] = useContext(ThemeContext)
  const [show, setShow] = useState(false);

  const handleNavbarToggler = () => setShow(s => !s)
  const isShow = show ? 'show' : ''

  useEffect(() => {
    document.body.className = `bg-${theme}`
  }, [theme])

  function switchTheme() {
    setTheme(theme === DARK ? LIGHT : DARK)
  }

  const iconClasses = classNames({
    'bi-moon-stars': theme === LIGHT,
    'bi-sun': theme === DARK
  })

  // let navbarTheme = theme === DARK ? LIGHT : DARK

  return (
    <nav className={`bg-${DARK} navbar navbar-${DARK} navbar-expand-sm py-3`}>
      <div className="container">
        <button className="navbar-toggler" type="button" onClick={handleNavbarToggler}>
          <span className="navbar-toggler-icon"></span>
        </button>
        <div className={`collapse navbar-collapse order-1 order-sm-0 row ${isShow}`} id="navbarSupportedContent">
          <div className="navbar-nav ps-2 ms-1 ps-sm-0 ms-sm-0">
            <Link to="/" className="nav-item nav-link">Портфолио</Link>
          </div>
        </div>
        <div className={`btn-group col-2 justify-content-end pe-3 pe-sm-0 text-${LIGHT}`} role="group">
          <i className={`${iconClasses}`}
            role='button'
            onClick={switchTheme}
            title='переключить цвет.тему'></i>
        </div>
      </div>
    </nav>
  )
}