import React, { useContext } from 'react'

import { ThemeContext, CurrentUserContext, LIGHT, DARK } from 'contexts'
import './person.css'

export default function Person() {
  const [theme,] = useContext(ThemeContext)
  const [currentUser,] = useContext(CurrentUserContext)

  const borderColor = theme === DARK ? 'border-secondary' : ''
  const { avatar_url, name, company, email, location, blog, login } = currentUser

  return (
    <div className='person row'>
      <div className="col-3 col-sm-12 px-md-4">
        <img src={avatar_url}
          className={`img-fluid rounded-circle mb-4 border ${borderColor}`}
          alt={name} />
      </div>
      <div className="col-9 col-sm-12 px-md-4">
        <h1>
          <div itemProp="name" className='fs-2'>{name}</div>
          {/* <div itemProp="additionalName" className={`fs-5 fw-light`}>{login}</div> */}
        </h1>
        <ul className={`list-unstyled`}>
          <li>
            <i className="bi-building me-1"></i>
            <small>{company}</small>
          </li>
          <li>
            <i className="bi-geo-alt me-1"></i>
            <small>{location}</small>
          </li>
          <li>
            <i className="bi-envelope me-1"></i>
            <a className='person-link' href="mailto:brew@bk.ru"><small>brew@bk.ru</small></a>
          </li>
          <li className="text-truncate">
            <i className="bi-link-45deg me-1"></i>
            <a className='person-link' href={blog}><small>{blog}</small></a>
          </li>
        </ul>
      </div>
    </div>
  )
}