import React, { useContext } from 'react'

import { ThemeContext, DARK } from 'contexts'

import './project.css'

export default function Project({ project: { title, url, description, favicon } }) {
  const [theme,] = useContext(ThemeContext)

  const borderColor = theme === DARK ? 'border-secondary' : ''

  return (
    <div className="col">
      <div className={`project card shadow-sm h-100 bg-transparent ${borderColor}`}>
        <div className="card-body">
          <h3 className="align-items-baseline card-title d-flex fs-5 fw-normal">
            <img src={favicon} className="project-img me-2" />
            {title}
          </h3>
          <p className="card-text">{description}</p>
        </div>
        <a href={url} target='_blank' className="stretched-link"></a>
      </div>
    </div>
  )
}