import React, { useEffect } from 'react'
import { Link } from 'react-router-dom'

import { useFetch } from 'hooks/useFetch'

export default function Repo({ repo }) {
  const { name } = repo
  const apiUrl = `/repos/weynemeynen/${name}/contents`
  const [{ response, isLoading, error }, doFetch] = useFetch(apiUrl)

  console.log('response Repo contents', response)

  useEffect(() => {
    doFetch()
  }, [doFetch, repo])

  const dirs = response.map((dir) => {
    if (dir.type === 'dir') {
      return <Link
        className="btn btn-lg btn-outline-info me-2 mb-2"
        to={`#`}
        role="button"
        key={dir.name}>
        {dir.name}
      </Link>
    }
  })

  return (
    <>
      <h1 className='my-4 ps-3'>{name}</h1>
      <div className='directories ps-3'>{dirs}</div>
    </>
  )
}