import React from 'react'
import { NavLink, withRouter } from 'react-router-dom'

import { routesMap } from 'routes'

const LinkButton = ({
  history,
  match,
  to,
  staticContext,
  selected,
  className,
  ...other
}) => {
  const select = match.url === routesMap.home || match.url === routesMap.repositories ? selected : ''

  return <NavLink to={to} className={className + ' ' + select} activeClassName={selected} {...other} />
}

export default withRouter(LinkButton)
