import React from 'react'
import { NavLink } from 'react-router-dom'

import './toggler.css'

export default function Toggler({ repoName }) {

  return (
    <nav className="toggler nav position-relative">
      <NavLink to='/' className="nav-link text-reset active border-bottom border-2 border-warning">Repositories</NavLink>
      {repoName && (
        <NavLink to={`/repositories/${repoName}`} className="nav-link text-reset" exact>{repoName}</NavLink>
      )}
    </nav>
  )
}