import React, { useContext } from 'react'
import { NavLink } from 'react-router-dom'
import classNames from 'classnames'

import { ThemeContext, LIGHT, DARK } from 'contexts'

import './toggler.css'

export default function Toggler({ reposCount }) {
  const [theme,] = useContext(ThemeContext)

  const navLinkColor = theme === DARK ? 'var(--my-silver)' : 'var(--bs-secondary)'

  const selected = classNames('border-bottom', 'border-2', 'border-danger', {
    [`text-${DARK}`]: theme === LIGHT,
    [`text-${LIGHT}`]: theme === DARK,
  })

  const bg = classNames({
    [`bg-light`]: theme === LIGHT,
    [`bg-secondary`]: theme === DARK,
  })
  const text = classNames({
    [`text-${DARK}`]: theme === LIGHT,
    [`text-${LIGHT}`]: theme === DARK,
  })

  return (
    <nav className="toggler nav position-relative">
      <NavLink to='/overview' className='nav-link text-decoration-none' activeClassName={selected} style={{ color: navLinkColor }}>
        <i className='bi-book me-2'></i>
        Обзор
      </NavLink>
      <NavLink to='/repositories' className='nav-link text-decoration-none' activeClassName={selected} style={{ color: navLinkColor }}>
        <i className='bi-journal-code me-2'></i>
      Репозитории
        {!!reposCount && (
          <span className={`badge rounded-pill ms-1 fw-normal ${bg} ${text}`}>{reposCount}</span>
        )}
      </NavLink>
      {/* <NavLink to='/projects' className='nav-link text-decoration-none' activeClassName={selected}  style={{color: navLinkColor}}>
      <i className="bi-kanban me-2"></i>
        Проекты
      </NavLink> */}
    </nav>
  )
}