import React from 'react'
import { NavLink } from 'react-router-dom'
import classNames from 'classnames'

import './toggler.css'

export default function Toggler({ repoName }) {

  const selected = classNames('border-bottom', 'border-2', 'border-warning')

  return (
    <nav className="toggler nav position-relative">
      <NavLink to='/' exact className='nav-link text-reset' activeClassName={selected}>Repositories</NavLink>
      {repoName && (
        <NavLink to={`/repos/${repoName}`} className='nav-link text-reset' activeClassName={selected}>{repoName}</NavLink>
      )}
    </nav>
  )
}