import React, { useContext } from 'react'

import { ThemeContext } from 'contexts'

import Person from 'components/Person'
import Repositories from 'components/Repositories'

export default function Home() {
  const [theme,] = useContext(ThemeContext)
  const textColor = theme === 'dark' ? 'light' : 'dark'

  return (
    <React.Fragment>
      <div className='layer border-bottom py-5'></div>
      <div className={`container flex-grow-1 px-lg-5 bg-${theme} text-${textColor}`}>
        <div className="row g-0 mx-lg-5 px-lg-5">
          <div className="col-sm-4 col-md-3" itemScope itemType="http://schema.org/Person">
            <Person />
          </div>
          <div className="col-sm-8 col-md-9">
            <Repositories />
          </div>
        </div>
      </div>
    </React.Fragment>
  )
}