import Overview from "pages/Overview";
import Repositories from "pages/Repositories"; // главная
import RepoPage from "pages/RepoPage";
// import DirPage from "pages/DirPage";
import Projects from "pages/Projects";

const routes = [
  {
    name: 'home',
    path: '/',
    component: Repositories,
    exact: true
  },
  {
    name: 'repositories',
    path: '/repositories',
    component: Repositories,
    exact: true
  },
  {
    name: 'repo',
    path: '/repositories/:repo',
    component: RepoPage,
    exact: true
  },
  // {
  //   name: 'dir',
  //   path: '/repositories/:repo/:dir',
  //   component: DirPage,
  //   exact: true
  // },
  {
    name: 'projects',
    path: '/projects',
    component: Projects,
    exact: true
  },
  {
    name: 'overview',
    path: '/overview',
    component: Overview,
    exact: true
  }
]

const routesMap = {}

routes.forEach(route => {
  if (route.hasOwnProperty('name')) {
    routesMap[route.name] = route.path
  }
})

export { routesMap }
export default routes
